public class Pneumatics extends Subsystem {

    private final Compressor compressor = RobotMap.pneumaticsCompressor;
    private final DoubleSolenoid doubleSolenoid = RobotMap.pneumaticsDoubleSolenoid;

    @Override
    public void initDefaultCommand() {
    
    }

    @Override
    public void periodic() {	
//    	boolean enabled = compressor.enabled();
//    	boolean pressureSwitch = compressor.getPressureSwitchValue();
//    	double current = compressor.getCompressorCurrent();
    }
    
    public void up() {
        if (doubleSolenoid != null) doubleSolenoid.set(DoubleSolenoid.Value.kForward);   
    }
    
    public void down() {
    	if (doubleSolenoid != null) doubleSolenoid.set(DoubleSolenoid.Value.kReverse);   
    } 
    
    public void off() {
    	if (doubleSolenoid != null) doubleSolenoid.set(DoubleSolenoid.Value.kOff);   
    }
}

